static void update_label4_from_file(lv_obj_t* label) {
    FILE* file = fopen("/tmp/5.txt", "r");
    if (!file) {
        lv_label_set_text(label, "0");
        return;
    }

    char buffer[32];
    int line = 0;
    // 读取到第三行
    while (fgets(buffer, sizeof(buffer), file)) {
        if (++line == 3) {
            // 去除换行符
            buffer[strcspn(buffer, "\n")] = '\0';
            lv_label_set_text(label, buffer);
            fclose(file);
            return;
        }
    }
    
    fclose(file);
    lv_label_set_text(label, "0");  // 默认值
}

