// 文件读取和格式化函数
static void update_label3_from_file(lv_obj_t* label) {
    // 打开文件
    FILE* file = fopen("/tmp/3.txt", "r");
    if (!file) {
        lv_label_set_text(label, "文件未找到");
        return;
    }

    // 读取内容
    char raw_text[32];  // 假设文件内容不超过32字节
    if (fgets(raw_text, sizeof(raw_text), file) == NULL) {
        lv_label_set_text(label, "文件内容为空");
        fclose(file);
        return;
    }
    fclose(file);

    // 分割日期和时间
    char* date = strtok(raw_text, " ");
    char* time = strtok(NULL, "\n");  // 处理可能的换行符

    // 格式化输出
    if (date && time) {
        char formatted_text[64];
        snprintf(formatted_text, sizeof(formatted_text), "%s\n\n\n      %s", date, time);
        lv_label_set_text(label, formatted_text);
    } else {
        lv_label_set_text(label, "时间格式错误");
    }
}

