// 查找匹配的中文城市名称
static const char* find_chinese_city(const char* input) 
{
    if (!input) return NULL;

    // 遍历映射表
    for (int i = 0; strlen(city_mappings[i].pinyin) > 0; i++) {
        if (strcmp(input, city_mappings[i].pinyin) == 0) {
            return city_mappings[i].chinese;
        }
    }
    return NULL;  // 未找到匹配项
}


