void ui_event_TextArea3(lv_event_t * e){
    if (!e || !e->target) {
        printf("Error: Invalid event or target\n");
        return;
    }

    lv_event_code_t event_code = lv_event_get_code(e);
    lv_obj_t * target = lv_event_get_target(e);
    if(event_code == LV_EVENT_CLICKED) {
        _ui_keyboard_set_target(ui_Keyboard1,ui_TextArea3);
        _ui_flag_modify(ui_Keyboard1, LV_OBJ_FLAG_HIDDEN, _UI_MODIFY_FLAG_REMOVE);
        _ui_flag_modify(ui_Button12, LV_OBJ_FLAG_HIDDEN, _UI_MODIFY_FLAG_REMOVE);
        _ui_flag_modify(ui_Image9, LV_OBJ_FLAG_HIDDEN, _UI_MODIFY_FLAG_REMOVE);
    }
    else if (event_code == LV_EVENT_DEFOCUSED) {
        // 获取当前文本
        const char *input_text = lv_textarea_get_text(ui_TextArea3);
        if (!input_text) {
            printf("Error: TextArea text is NULL\n");
            return;
        }
        
     // 新增：将拼音写入文件（带错误处理）
     const char *filepath = "/tmp/4.txt";
     FILE *fp = fopen(filepath, "w");
     if (fp) {
         if (fprintf(fp, "%s", input_text) < 0) {
             perror("写入文件失败");
         }
         fclose(fp);
     } else {
         perror("打开文件失败");
         printf("失败路径: %s\n", filepath);
     }

        // 查找匹配的中文城市名称
        const char *chinese_city = find_chinese_city(input_text);
        if (chinese_city) {
            // 设置匹配后的中文内容
            lv_textarea_set_text(ui_TextArea3, chinese_city);
        } else {
            printf("Warning: No matching city found for input: %s\n", input_text);
        }

        // 强制刷新显示
        lv_event_send(ui_TextArea3, LV_EVENT_VALUE_CHANGED, NULL);
    }
}
