void ui_event_TextArea1(lv_event_t * e) {
    lv_event_code_t event_code = lv_event_get_code(e);
    lv_obj_t * target = lv_event_get_target(e);

    if (event_code == LV_EVENT_PRESSED) {
        _ui_flag_modify(ui_Keyboard2, LV_OBJ_FLAG_HIDDEN, _UI_MODIFY_FLAG_REMOVE);
        _ui_flag_modify(ui_Button10, LV_OBJ_FLAG_HIDDEN, _UI_MODIFY_FLAG_REMOVE);
    } 
    else if (event_code == LV_EVENT_CLICKED) {
        _ui_keyboard_set_target(ui_Keyboard2, ui_TextArea1);
    }
    else if (event_code == LV_EVENT_DEFOCUSED) {  // 当TextArea失去焦点时保存文本
        const char *text = lv_textarea_get_text(ui_TextArea1);  // 获取文本内容
        
        // 写入文件
        FILE *file = fopen("/tmp/1.txt", "w");  // 以追加模式打开文件
        if (file != NULL) {
            fprintf(file, "%s\n", text);  // 写入文本并换行
            fclose(file);                // 关闭文件
            //lv_label_set_text(ui_Label1, "Saved to output.txt!");  // 可选：显示保存成功的提示
        } else {
            lv_label_set_text(ui_Label1, "Error: Cannot open file!");  // 错误提示
        }
    }

}
